import React, { Component } from 'react'
import { ExternalLink } from 'react-external-link';


class Slideshow extends Component{


    constructor(props){
        super(props);
        this.state = {
          data:[],
          works:[],
          counter:0
        }
        this.initialSet = this.initialSet.bind(this);
      }


    async componentDidMount(){
      const response = await fetch(`https://raw.githubusercontent.com/fernandocomet/website/master/fernandocomet/src/data/portfolio.json`);
      const json = await response.json();
      this.setState({ 
          data: json,
          counter: 0 
        });
      this.initialSet();
    }


    initialSet(){

        let worksArr = [];
        for (let i = 0; i < this.state.data.length ; i++){
          worksArr.push(this.state.data[i])
        }
        this.setState({
          works: worksArr
        })
    }


    render(){

        return(
            <div className="container">
                 <div className="row bg-success">hola</div>
            </div>
        )
    }

}



export default Slideshow;
