import React, { Component } from 'react'
import { ExternalLink } from 'react-external-link';
import './RollItem.css';

class RollItem extends Component{

    render(){
        return(
            <div className="mb-5">
                <div className="row">
                    <div className="col"><h1>{this.props.title}</h1></div>
                </div>
                <div className="row">
                    <img src={this.props.image} alt={this.props.title} className="img-fluid" />
                </div>
                <div className="row">
                    <div className="col-md-9">{this.props.description}</div>
                    <div className="col-md-3 text-right">#{this.props.category}</div>
                </div>
                <div className="row">
                    <div className="col">{this.props.description2}</div>
                </div>
                <div className="row">
                    <div className="col-md-9">{this.props.thanksto}</div>
                    <div className="col-md-3 text-right"> <a href="#" className="btn inverse">Inverse</a></div>
                </div>
                <hr></hr>
            </div>
        )
    }
}

export default RollItem;

