import React, { Component } from 'react'
import { ExternalLink } from 'react-external-link';

class RollItem extends Component{

    render(){
        return(
            <div >
                <div className="row bg-info">
                    <div className="col-md-9 bg-warning"><h1>{this.props.title}</h1></div>
                    <div className="col-md-3 bg-success">#{this.props.category}</div>
                </div>
                <div className="row bg-success">
                    <img src={this.props.image} alt={this.props.title} className="img-fluid" />
                </div>
                <div className="row bg-warning">{this.props.description}</div>
                <div className="row bg-danger">{this.props.description2}</div>
                <div className="row bg-info">
                    <div className="col-md-9 bg-warning">{this.props.thanksto}</div>
                    <div className="col-md-3 bg-success">button</div>
                </div>
            </div>
        )
    }
}

export default RollItem;

